mapper.select:
select *
from (
	select rownum rn, TMP.*
	from (
		select *
		from board
		where board_head = '공지'
		order by board_no desc
	)TMP
) 
where rn between #{begin} and #{end}

mapper.insert:
insert into board(
	board_no, board_writer, board_title, board_content, board_head,
	board_time, board_read, board_like, board_reply, board_group,
	board_parent, board_depth
)
values (
	#{boardNo}, #{boardWriter}, #{boardTitle}, #{boardContent}, #{boardHead},
	sysdate, 0, 0, 0, #{boardGroup}, #{boardParent, jdbcType=NUMERIC}, #{boardDepth}
)

mapper.update:
update board
set board_head = #{boardHead}, board_title = #{boardTitle},
	board_content = #{boardContent}
where board_no = #{boardNo}

update board
set board_read = board_read + 1
where board_no = #{boardNo}

select.if:
select *
from (
	select rownum rn, TMP.*
	from (
		select *
		from board
		where instr(#{column}, #{keyword}) > 0
		connect by prior board_no = board_parent
		start with board_parent is null
		order siblings by board_group desc, board_no asc
	)TMP
)
where rn between #{begin} and #{end}

select *
from (
	select rownum rn, TMP.*
	from (
		select *
		from board
		connect by prior board_no = board_parent
		start with board_parent is null
		order siblings by board_group desc, board_no asc
	)TMP
)
where rn between #{begin} and #{end}

mapper.update:
update board
set board_like = #{count}
where board_no = #{boardNo}

update board
set board_reply = (
	select count(*)
	from reply
	where reply_origin = #{boardNo}
)
where board_no = #{boardNo}
