mapper.insert:
insert into log(
	emp_no, log_login, log_ip, log_browser
)
values(
	#{empNo}, #{logLogin}, #{logIp}, #{logBrowser}
)

mapper.select:
select * from search_log_info
and emp_name = #{empName}
and log_login between to_date( to_char(sysdate - #{searchLoginDays}, 'YYYY-MM-DD'), 'YYYY-MM-DD' ) and sysdate
