mapper.select:
SELECT * FROM trip WHERE emp_no
= #{empNo} AND status = 1 AND EXTRACT(YEAR FROM usedDate) =
#{selectedValue}

select trip_no from (select trip_no
from trip where emp_no= #{empNo} order by trip_no DESC) where ROWNUM =
1

mapper.update:
UPDATE trip
SET status = #{status}
WHERE trip_no = #{tripNo}
